mapper.select:
select
id,name,value,`type`,description,sort,parent_id,create_by,create_date,update_by,update_date,remarks,del_flag,del_flag
from sys_dict

	id = #{id}
	name = #{name}
	value = #{value}
	type = #{type}
	description = #{description}
	sort = #{sort}
	parent_id = #{parent_id}
	create_by = #{create_by}
	create_date = #{create_date}
	update_by = #{update_by}
	update_date = #{update_date}
	remarks = #{remarks}
	del_flag = #{del_flag}
	del_flag = #{del_flag}

limit #{offset},#{limit}
