mapper.select:
select
`user_id`,`username`,`name`,`password`,`dept_id`,`email`,`mobile`,`status`,`user_id_create`,`gmt_create`,`gmt_modified`,`sex`,`birth`,`pic_id`,`live_address`,`hobby`,`province`,`city`,`district`
from sys_user

	and user_id=#{user_id}
	and username=#{username}
	and name=#{name}
	and dept_id=#{dept_id}
	and password=#{password}
	and email=#{email}
	and mobile=#{mobile}
	and status=#{status}
	and user_id_create=#{user_id_create}
	and gmt_create=#{gmt_create}
	and gmt_modified=#{gmt_modified}
	and sex=#{sex}
	and birth=#{birth}
	and pic_id=#{pic_id}
	and live_address=#{live_address}
	and hobby=#{hobby}
	and province=#{province}
	and city=#{city}
	and district=#{district}
